cur.execute("INSERT INTO TALKS (MESSAGE, TYPE) VALUES ('Солидарен с тобой, смотри на картинку, и станет понятно, в чём управленческая ошибка, в зависимости от причины невыполнения задачи', 'TEXT');")
cur.execute("INSERT INTO TALKS (MESSAGE, TYPE) VALUES ('Теперь ты знаешь, что нужно делать, чтобы избежать таких досадных управленческих недоразумений и в разы увеличить процент выполненных задач. Для этого нужно знать и выполнять ключевые управленческие функции', 'TEXT');")
cur.execute("INSERT INTO TALKS (MESSAGE, TYPE) VALUES ('Ну что ж, теперь ты знаешь, какие есть функции у руководителя и какой функции нужно уделить больше внимания, если сотрудник не выполняет задачу.', 'TEXT');")
