cur.execute("INSERT INTO TALKS (MESSAGE, TYPE) VALUES ('Чтобы сотрудник понимал, что ему делать и чётко видел образ конечного результата, руководителю нужно грамотно ставить цели.', 'TEXT');")
cur.execute("INSERT INTO TALKS (MESSAGE, TYPE) VALUES ('Спасибо, что ответил на все вопросы! И у тебя {N} правильных ответов из 3', 'TEXT');")
